  return mdx(MDXLayout, _extends({}, layoutProps, props, {
    components: components,
    mdxType: "MDXLayout"
  }), mdx(FontAwesomeIcon, {
    icon: ['fas', 'star'],
    size: "lg",
    mdxType: "FontAwesomeIcon"
  }), mdx(FontAwesomeIcon, {
    icon: ['fas', 'star'],
    size: "lg",
